import React, { useState, useEffect } from "react";
import axios from "axios";

function Cadastros() {
    const [clientes, setClientes] = useState([]);
    const [animais, setAnimais] = useState([]);
    const [produtos, setProdutos] = useState([]);
    const [formulario, setFormulario] = useState(null);

    useEffect(() => {
        // Carregar dados dos clientes, animais e produtos
        axios.get("/api/clientes")
            .then(response => setClientes(response.data))
            .catch(error => console.error("Erro ao carregar clientes:", error));
        
        axios.get("/api/animais")
            .then(response => setAnimais(response.data))
            .catch(error => console.error("Erro ao carregar animais:", error));

        axios.get("/api/produtos")
            .then(response => setProdutos(response.data))
            .catch(error => console.error("Erro ao carregar produtos:", error));
    }, []);

    function getFormulario(tipo) {
        if (tipo === "cliente") {
            return (
                <form>
                    <input type="text" placeholder="Nome" />
                    <input type="text" placeholder="Telefone" />
                    <input type="email" placeholder="Email" />
                    <button type="submit">Salvar</button>
                </form>
            );
        } else if (tipo === "animal") {
            return (
                <form>
                    <input type="text" placeholder="Nome" />
                    <input type="text" placeholder="Raça" />
                    <input type="number" placeholder="Idade" />
                    <button type="submit">Salvar</button>
                </form>
            );
        } else if (tipo === "produto") {
            return (
                <form>
                    <input type="text" placeholder="Nome do Produto" />
                    <input type="text" placeholder="Descrição" />
                    <input type="text" placeholder="Preço" />
                    <button type="submit">Salvar</button>
                </form>
            );
        }
    }

    function excluirCadastro(id, tipo) {
        axios.delete(`/api/${tipo}/${id}`)
            .then(() => {
                if (tipo === "cliente") {
                    setClientes(clientes.filter(cliente => cliente.id !== id));
                } else if (tipo === "animal") {
                    setAnimais(animais.filter(animal => animal.id !== id));
                } else if (tipo === "produto") {
                    setProdutos(produtos.filter(produto => produto.id !== id));
                }
            })
            .catch(error => console.error(`Erro ao excluir ${tipo}:`, error));
    }

    function editarCadastro(cadastro, tipo) {
        setFormulario({ tipo, cadastro });
    }

    function novoCadastro(tipo) {
        setFormulario(tipo);
    }

    function getLinha(cadastro, tipo) {
        if (tipo === "cliente") {
            return (
                <tr key={cadastro.id}>
                    <td>{cadastro.id}</td>
                    <td>{cadastro.nome}</td>
                    <td>{cadastro.telefone}</td>
                    <td>{cadastro.email}</td>
                    <td>
                        <button onClick={() => excluirCadastro(cadastro.id, "cliente")}>Excluir</button>
                        <button onClick={() => editarCadastro(cadastro, "cliente")}>Editar</button>
                    </td>
                </tr>
            );
        } else if (tipo === "animal") {
            return (
                <tr key={cadastro.id}>
                    <td>{cadastro.id}</td>
                    <td>{cadastro.nomeAnimal}</td>
                    <td>{cadastro.raca}</td>
                    <td>{cadastro.idade}</td>
                    <td>
                        <button onClick={() => excluirCadastro(cadastro.id, "animal")}>Excluir</button>
                        <button onClick={() => editarCadastro(cadastro, "animal")}>Editar</button>
                    </td>
                </tr>
            );
        } else if (tipo === "produto") {
            return (
                <tr key={cadastro.id}>
                    <td>{cadastro.id}</td>
                    <td>{cadastro.nomeProduto}</td>
                    <td>{cadastro.preco}</td>
                    <td>{cadastro.descricao}</td>
                    <td>
                        <button onClick={() => excluirCadastro(cadastro.id, "produto")}>Excluir</button>
                        <button onClick={() => editarCadastro(cadastro, "produto")}>Editar</button>
                    </td>
                </tr>
            );
        }
    }

    return (
        <div>
            <h1>Cadastro de Clientes</h1>
            <button onClick={() => novoCadastro("cliente")}>Novo Cliente</button>
            {formulario === "cliente" && getFormulario("cliente")}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Telefone</th>
                        <th>Email</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {clientes.map((cliente) => getLinha(cliente, "cliente"))}
                </tbody>
            </table>

            <h1>Cadastro de Animais</h1>
            <button onClick={() => novoCadastro("animal")}>Novo Animal</button>
            {formulario === "animal" && getFormulario("animal")}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Raça</th>
                        <th>Idade</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {animais.map((animal) => getLinha(animal, "animal"))}
                </tbody>
            </table>

            <h1>Cadastro de Produtos</h1>
            <button onClick={() => novoCadastro("produto")}>Novo Produto</button>
            {formulario === "produto" && getFormulario("produto")}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Preço</th>
                        <th>Descrição</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {produtos.map((produto) => getLinha(produto, "produto"))}
                </tbody>
            </table>
        </div>
    );
}

export default Cadastros;
