import "./Cadastros.css"

import axios from "axios";
import { useState, useEffect } from "react";

function Cadastros() {

    const [pessoa, setPessoas] = useState(null);
    const [pessoas, setClientes] = useState([]);

    function getPessoas() {
        axios.get("http://localhost:5119/clientes")
            .then((resposta) => {
                setClientes(resposta.data);
            });
    }

    useEffect(getPessoas, []);


    function novaPessoa() {
        setPessoas(
            {
                nome: "",
                cpf: "",
                telefone: "",
                email: ""
            }
        );
    }

    function cancelar() {
        setPessoas(null);
    }

    function refresh() {
        cancelar();
        getPessoas();
    }

    function onChangeCliente(campo, valor, id) {
        pessoa[campo] = valor;
        setPessoas({
            ...pessoa,
        });
    }

    function salvarCliente() {
        if (pessoa.id) {
            axios.put("http://localhost:5119/pessoas/" + pessoa.id, pessoa)
                .then(() => {
                    refresh();
                });
        } else {
            axios.post("http://localhost:5119/pessoas", pessoa)
                .then(() => {
                    refresh();
                });
        }
    }


    function getFormulario() {
        return (
            <form>
                <label for="nome">Nome</label>
                <input type="text" id="nome" name="nome"
                    value={pessoa.nome}
                    onChange={(e) => {
                        onChangeCliente(e.target.name, e.target.value, pessoa.id);
                    }}
                />
                <label for="cpf">CPF</label>
                <input type="text" id="cpf" name="cpf"
                    value={pessoa.cpf}
                    onChange={(e) => {
                        onChangeCliente(e.target.name, e.target.value, pessoa.id);
                    }}
                />
                <label for="telefone">Telefone</label>
                <input type="text" id="telefone" name="telefone"
                    value={pessoa.telefone}
                    onChange={(e) => {
                        onChangeCliente(e.target.name, e.target.value, pessoa.id);
                    }}
                />
                <label for="email">E-mail</label>
                <input type="text" id="email" name="email"
                    value={pessoa.email}
                    onChange={(e) => {
                        onChangeCliente(e.target.name, e.target.value, pessoa.id);
                    }}
                />
                <button onClick={() => { cancelar(); }}>Cancelar</button>
                <button onClick={() => { salvarCliente(); }} >Salvar</button>
            </form>
        );
    }

    function excluirPessoa(id) {
        axios.delete("http://localhost:5119/pessoas/" + id).then(
            () => {
                getPessoas();
            }
        );
    }

    function editarPessoas(pessoas) {
        setPessoas(pessoas);
    }


    function getLinha(pessoas) {
        return (
            <tr>
                <td>{pessoas.id}</td>
                <td>{pessoas.nome}</td>
                <td>{pessoas.cpf}</td>
                <td>{pessoas.telefone}</td>
                <td>{pessoas.email}</td>
                <td>
                    <button onClick={
                        () => {
                            excluirPessoa(pessoas.id);
                        }
                    }>Excluir</button>
                    <button onClick={() => { editarPessoas(pessoas); }}>Editar</button>
                </td>
            </tr>
        );
    }

    function getLinhas() {
        const linhasDaTabela = [];
        for (let i = 0; i < pessoas.length; i++) {
            const cliente = pessoas[i];
            linhasDaTabela[i] = getLinha(cliente);
        }
        return linhasDaTabela;
    }

    function getTabela() {
        return (
            <table>
                <tr>
                    <th>ID</th>
                    <th>Nome</th>
                    <th>CPF</th>
                    <th>Ações</th>
                </tr>
                {getLinhas()}
            </table>
        );
    }




    function getConteudo() {
        if (pessoa == null) {
            return (
                <>
                    <button onClick={() => { novaPessoa(); }}>Novo</button>
                    {getTabela()}
                </>
            );

        } else {
            return getFormulario();
        }
    }

    return (
        <div>
            <h1>Cadastro de clientes</h1>
            {getConteudo()}
        </div>

    );
}

export default Cadastros;
